import React from 'react';
import { useQuery, gql } from '@apollo/client';
import DeleteComptes from './DeleteComptes';

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = ({ closePopup, refetch }) => {
    const { loading, error, data } = useQuery(GET_ALL_COMPTES);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p style={{ color: 'red' }}>Erreur : {error.message}</p>;

    return (
        <div style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: '#ffffff',
            borderRadius: '10px',
            boxShadow: '0px 4px 20px rgba(0, 0, 0, 0.1)',
            width: '80%',
            maxWidth: '600px',
            padding: '20px',
            zIndex: '9999',
            overflowY: 'auto',
        }}>
            <button
                onClick={closePopup}
                style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    backgroundColor: 'transparent',
                    color: '#333',
                    fontSize: '20px',
                    border: 'none',
                    cursor: 'pointer',
                }}
            >
                X
            </button>
            <h2 style={{ textAlign: 'center', color: '#5e4b8b' }}>Liste des Comptes</h2>
            <table style={{
                width: '100%',
                borderCollapse: 'collapse',
                marginTop: '20px',
                border: '1px solid #ddd',
                borderRadius: '8px',
            }}>
                <thead>
                <tr>
                    <th style={{ padding: '10px', backgroundColor: '#f5f5f5' }}>ID</th>
                    <th style={{ padding: '10px', backgroundColor: '#f5f5f5' }}>Solde</th>
                    <th style={{ padding: '10px', backgroundColor: '#f5f5f5' }}>Date de Cr√©ation</th>
                    <th style={{ padding: '10px', backgroundColor: '#f5f5f5' }}>Type de Compte</th>
                    <th style={{ padding: '10px', backgroundColor: '#f5f5f5' }}>Actions</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id}>
                        <td style={{ padding: '10px' }}>{compte.id}</td>
                        <td style={{ padding: '10px' }}>{compte.solde}</td>
                        <td style={{ padding: '10px' }}>{compte.dateCreation}</td>
                        <td style={{ padding: '10px' }}>{compte.typeCompte}</td>
                        <td style={{ padding: '10px' }}>
                            <DeleteComptes id={compte.id} refetch={refetch} />
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListComptes;
