import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch, showPopup, setShowPopup }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    const closePopup = () => {
        setShowPopup(false); // Close the popup
    };

    return (
        <div
            style={{
                display: showPopup ? 'flex' : 'none',
                justifyContent: 'center',
                alignItems: 'center',
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                zIndex: 50,
                animation: 'fadeIn 0.5s forwards',
            }}
        >
            <div
                style={{
                    backgroundColor: 'white',
                    padding: '20px',
                    borderRadius: '10px',
                    boxShadow: '0px 10px 30px rgba(0, 0, 0, 0.1)',
                    width: '80%',
                    maxWidth: '400px',
                    animation: 'page-turn 0.5s forwards',
                }}
            >
                <button
                    onClick={closePopup}
                    style={{
                        position: 'absolute',
                        top: '10px',
                        right: '10px',
                        backgroundColor: 'transparent',
                        border: 'none',
                        fontSize: '20px',
                        cursor: 'pointer',
                    }}
                >
                    X
                </button>
                <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '16px' }}>
                    Ajouter un Compte
                </h2>
                <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                    <div>
                        <label>Solde :</label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            required
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px',
                            }}
                        />
                    </div>
                    <div>
                        <label>Date de Création :</label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            required
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px',
                            }}
                        />
                    </div>
                    <div>
                        <label>Type de Compte :</label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px',
                            }}
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        style={{
                            backgroundColor: '#4CAF50',
                            color: 'white',
                            padding: '12px 20px',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            border: 'none',
                            marginTop: '10px',
                        }}
                    >
                        {loading ? 'Ajout en cours...' : 'Ajouter'}
                    </button>
                    {error && <p style={{ color: 'red' }}>Erreur : {error.message}</p>}
                </form>
            </div>
        </div>
    );
};

export default AddCompte;
