import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch(); // Refresh the list
            setSolde("");
            setDateCreation("");
            setTypeCompte("COURANT");
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div
            style={{
                background: "linear-gradient(135deg, #e0c3fc, #8ec5fc)",
                padding: "30px",
                borderRadius: "15px",
                boxShadow: "0px 10px 20px rgba(0,0,0,0.2)",
                animation: "fadeIn 1s ease-in-out",
                margin: "20px",
            }}
        >
            <h2
                style={{
                    fontFamily: "'Poppins', sans-serif",
                    textAlign: "center",
                    color: "#333",
                }}
            >
                Ajouter un Compte
            </h2>
            <form
                onSubmit={handleSubmit}
                style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "20px",
                }}
            >
                <div style={{ position: "relative" }}>
                    <label
                        style={{
                            fontFamily: "'Poppins', sans-serif",
                            fontWeight: "500",
                            marginBottom: "5px",
                            display: "block",
                        }}
                    >
                        Solde
                    </label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "8px",
                            border: "1px solid #ddd",
                            outline: "none",
                            transition: "0.3s",
                        }}
                    />
                </div>
                <div>
                    <label
                        style={{
                            fontFamily: "'Poppins', sans-serif",
                            fontWeight: "500",
                            marginBottom: "5px",
                            display: "block",
                        }}
                    >
                        Date de Cr√©ation
                    </label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "8px",
                            border: "1px solid #ddd",
                            outline: "none",
                            transition: "0.3s",
                        }}
                    />
                </div>
                <div>
                    <label
                        style={{
                            fontFamily: "'Poppins', sans-serif",
                            fontWeight: "500",
                            marginBottom: "5px",
                            display: "block",
                        }}
                    >
                        Type de Compte
                    </label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "8px",
                            border: "1px solid #ddd",
                            outline: "none",
                            transition: "0.3s",
                        }}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Epargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        padding: "12px",
                        borderRadius: "8px",
                        backgroundColor: "#4caf50",
                        color: "#fff",
                        border: "none",
                        cursor: "pointer",
                        fontFamily: "'Poppins', sans-serif",
                        fontWeight: "600",
                        fontSize: "16px",
                        transition: "0.3s",
                    }}
                >
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && (
                    <p
                        style={{
                            color: "red",
                            fontFamily: "'Poppins', sans-serif",
                            textAlign: "center",
                        }}
                    >
                        Erreur : {error.message}
                    </p>
                )}
            </form>
        </div>
    );
};

export default AddCompte;
