import React from "react";
import { useQuery, gql } from "@apollo/client";
import { FaPiggyBank } from "react-icons/fa";

const GET_COMPTES = gql`
  query {
    allComptes {
      id
      solde
      type
      dateCreation
    }
  }
`;

const styles = {
    container: {
        maxWidth: "1200px",
        margin: "20px auto",
        padding: "20px",
        fontFamily: "'Arial', sans-serif",
    },
    title: {
        fontSize: "32px",
        fontWeight: "bold",
        color: "#4f46e5",
        display: "flex",
        alignItems: "center",
        gap: "10px",
    },
    grid: {
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
        gap: "20px",
        marginTop: "20px",
    },
    card: {
        background: "#fff",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        padding: "20px",
        transition: "transform 0.3s, box-shadow 0.3s",
    },
    cardHover: {
        transform: "scale(1.05)",
        boxShadow: "0 8px 16px rgba(0, 0, 0, 0.2)",
    },
    cardTitle: {
        fontSize: "20px",
        fontWeight: "bold",
        color: "#333",
    },
    cardText: {
        fontSize: "16px",
        color: "#555",
        marginTop: "10px",
    },
};

function Comptes() {
    const { loading, error, data } = useQuery(GET_COMPTES);

    if (loading) return <p style={{ textAlign: "center", color: "#4f46e5" }}>Chargement...</p>;
    if (error) return <p style={{ textAlign: "center", color: "red" }}>Erreur : {error.message}</p>;

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>
                <FaPiggyBank style={{ color: "#f43f5e" }} />
                Liste des Comptes
            </h2>
            <div style={styles.grid}>
                {data.allComptes.map((compte) => (
                    <div
                        key={compte.id}
                        style={{
                            ...styles.card,
                            ":hover": styles.cardHover,
                        }}
                    >
                        <h3 style={styles.cardTitle}>{compte.type}</h3>
                        <p style={styles.cardText}>Solde : {compte.solde.toFixed(2)} DH</p>
                        <p style={{ ...styles.cardText, fontSize: "14px", color: "#888" }}>
                            Créé le : {compte.dateCreation}
                        </p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Comptes;
