import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation SaveCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

function AddCompte() {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');
    const [addCompte, { data, loading, error }] = useMutation(ADD_COMPTE);

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    type: typeCompte,
                },
            },
        });
    };

    return (
        <div>
            <h2>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    placeholder="Solde"
                    value={solde}
                    onChange={(e) => setSolde(e.target.value)}
                />
                <input
                    type="date"
                    placeholder="Date de Création"
                    value={dateCreation}
                    onChange={(e) => setDateCreation(e.target.value)}
                />
                <select
                    value={typeCompte}
                    onChange={(e) => setTypeCompte(e.target.value)}
                >
                    <option value="COURANT">Courant</option>
                    <option value="EPARGNE">Épargne</option>
                </select>
                <button type="submit">Ajouter</button>
            </form>
            {loading && <p>Ajout en cours...</p>}
            {error && <p>Erreur : {error.message}</p>}
            {data && <p>Compte ajouté avec succès !</p>}
        </div>
    );
}

export default AddCompte;
