import React, { useState } from "react";
import { useQuery, gql } from "@apollo/client";
import AddCompte from "./AddCompte";
import DeleteCompte from "./DeleteComptes";

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const [showComptes, setShowComptes] = useState(false);
    const { loading, error, data, refetch } = useQuery(GET_ALL_COMPTES);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p style={styles.errorMessage}>Erreur : {error.message}</p>;

    return (
        <div style={styles.container}>
            <h2 style={styles.header}>Comptes Bancaires</h2>
            <AddCompte refetch={refetch} />
            <button
                onClick={() => setShowComptes(!showComptes)}
                style={styles.toggleButton}
            >
                {showComptes ? "Cacher les comptes" : "Voir tous les comptes"}
            </button>

            {showComptes && (
                <div style={styles.tableContainer}>
                    <table style={styles.table}>
                        <thead style={styles.tableHeader}>
                        <tr>
                            <th style={styles.tableHeaderCell}>ID</th>
                            <th style={styles.tableHeaderCell}>Solde</th>
                            <th style={styles.tableHeaderCell}>Date de Cr√©ation</th>
                            <th style={styles.tableHeaderCell}>Type de Compte</th>
                            <th style={styles.tableHeaderCell}>Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {data.allComptes.map((compte) => (
                            <tr key={compte.id} style={styles.tableRow}>
                                <td style={styles.tableCell}>{compte.id}</td>
                                <td style={styles.tableCell}>{compte.solde}</td>
                                <td style={styles.tableCell}>{compte.dateCreation}</td>
                                <td style={styles.tableCell}>{compte.typeCompte}</td>
                                <td style={styles.tableCell}>
                                    <DeleteCompte id={compte.id} refetch={refetch} />
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        marginTop: '20px',
        padding: '0 20px',
    },
    header: {
        textAlign: 'center',
        fontSize: '28px',
        fontWeight: '700',
        marginBottom: '20px',
    },
    toggleButton: {
        backgroundColor: '#3B82F6',
        color: 'white',
        padding: '12px',
        borderRadius: '8px',
        marginTop: '16px',
        cursor: 'pointer',
        fontWeight: '500',
    },
    tableContainer: {
        marginTop: '20px',
    },
    table: {
        width: '100%',
        borderCollapse: 'collapse',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        borderRadius: '8px',
    },
    tableHeader: {
        backgroundColor: '#3B82F6',
        color: 'white',
    },
    tableHeaderCell: {
        padding: '12px',
        textAlign: 'left',
    },
    tableRow: {
        borderBottom: '1px solid #ddd',
    },
    tableCell: {
        padding: '12px',
        textAlign: 'left',
    },
    errorMessage: {
        color: 'red',
        marginTop: '8px',
    },
};

export default ListComptes;
