import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ setView, refetch }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100vh',
            backgroundColor: '#f4f4f4',
        }}>
            <h2>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} style={{
                display: 'flex',
                flexDirection: 'column',
                gap: '15px',
                width: '300px',
                padding: '20px',
                background: 'white',
                borderRadius: '8px',
                boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
            }}>
                <input
                    type="number"
                    placeholder="Solde"
                    value={solde}
                    onChange={(e) => setSolde(e.target.value)}
                    style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ddd' }}
                    required
                />
                <input
                    type="date"
                    value={dateCreation}
                    onChange={(e) => setDateCreation(e.target.value)}
                    style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ddd' }}
                    required
                />
                <select
                    value={typeCompte}
                    onChange={(e) => setTypeCompte(e.target.value)}
                    style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ddd' }}
                >
                    <option value="COURANT">Courant</option>
                    <option value="EPARGNE">Ã‰pargne</option>
                </select>
                <button type="submit" style={{
                    padding: '10px',
                    backgroundColor: '#5e4b8b',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }} disabled={loading}>
                    {loading ? 'Ajout en cours...' : 'Ajouter'}
                </button>
                {error && <p style={{ color: 'red' }}>Erreur : {error.message}</p>}
            </form>
            <button
                onClick={() => setView('list')}
                style={{
                    marginTop: '20px',
                    backgroundColor: '#28a745',
                    color: 'white',
                    padding: '10px 20px',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
            >
                Voir tous les comptes
            </button>
        </div>
    );
};

export default AddCompte;
