import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";
import { toast } from "react-toastify";

const ADD_COMPTE = gql`
  mutation ($compte: CompteInput!) {
    saveCompte(compte: $compte) {
      id
      solde
      type
    }
  }
`;

function AddCompte() {
    const [solde, setSolde] = useState("");
    const [type, setType] = useState("COURANT");
    const [addCompte] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            toast.success("Compte ajouté avec succès !");
            setSolde("");
        },
        onError: (error) => {
            toast.error(`Erreur : ${error.message}`);
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({
            variables: { compte: { solde: parseFloat(solde), type } },
        });
    };

    return (
        <div className="max-w-2xl mx-auto bg-white shadow-md rounded-lg p-6 mt-10">
            <h2 className="text-2xl font-bold text-primary mb-4">Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700">
                        Solde (DH)
                    </label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-primary focus:border-primary"
                        required
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-700">
                        Type de Compte
                    </label>
                    <select
                        value={type}
                        onChange={(e) => setType(e.target.value)}
                        className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-primary focus:border-primary"
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    className="w-full bg-primary text-white font-bold py-2 px-4 rounded hover:bg-accent transition-colors"
                >
                    Ajouter le Compte
                </button>
            </form>
        </div>
    );
}

export default AddCompte;
