import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ togglePopup }) => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE);

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        }).then(() => {
            togglePopup(); // Fermer la popup après la soumission
        });
    };

    return (
        <div
            style={{
                position: "fixed",
                top: "0",
                left: "0",
                width: "100%",
                height: "100%",
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: "1000",
            }}
        >
            <div
                style={{
                    backgroundColor: "white",
                    padding: "20px",
                    borderRadius: "10px",
                    width: "400px",
                    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
                }}
            >
        <span
            onClick={togglePopup}
            style={{
                position: "absolute",
                top: "10px",
                right: "10px",
                fontSize: "20px",
                cursor: "pointer",
                color: "#aaa",
            }}
        >
          ×
        </span>
                <h3>Ajouter un Compte</h3>
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: "20px" }}>
                        <label>Solde :</label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            required
                            style={{
                                padding: "10px",
                                width: "100%",
                                borderRadius: "5px",
                                border: "1px solid #ddd",
                                marginTop: "5px",
                            }}
                        />
                    </div>
                    <div style={{ marginBottom: "20px" }}>
                        <label>Date de Création :</label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            required
                            style={{
                                padding: "10px",
                                width: "100%",
                                borderRadius: "5px",
                                border: "1px solid #ddd",
                                marginTop: "5px",
                            }}
                        />
                    </div>
                    <div style={{ marginBottom: "20px" }}>
                        <label>Type de Compte :</label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            style={{
                                padding: "10px",
                                width: "100%",
                                borderRadius: "5px",
                                border: "1px solid #ddd",
                                marginTop: "5px",
                            }}
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        style={{
                            backgroundColor: "#28a745",
                            color: "white",
                            padding: "10px 20px",
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                            width: "100%",
                            transition: "0.3s",
                        }}
                    >
                        {loading ? "Ajout en cours..." : "Ajouter"}
                    </button>
                    {error && <p style={{ color: "red", marginTop: "10px" }}>Erreur : {error.message}</p>}
                </form>
            </div>
        </div>
    );
};

export default AddCompte;
