import React from "react";
import { useQuery, gql } from "@apollo/client";
import DeleteCompte from "./DeleteCompte";

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const { loading, error, data, refetch } = useQuery(GET_ALL_COMPTES);

    if (loading) return <p style={{ textAlign: "center" }}>Chargement...</p>;
    if (error)
        return (
            <p style={{ textAlign: "center", color: "red" }}>Erreur : {error.message}</p>
        );

    return (
        <div
            style={{
                background: "linear-gradient(135deg, #fccb90, #d57eeb)",
                padding: "20px",
                borderRadius: "15px",
                boxShadow: "0px 10px 20px rgba(0,0,0,0.2)",
                margin: "20px",
                animation: "slideIn 1s ease-in-out",
            }}
        >
            <h2
                style={{
                    fontFamily: "'Poppins', sans-serif",
                    textAlign: "center",
                    color: "#333",
                }}
            >
                Liste des Comptes
            </h2>
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
                <thead>
                <tr>
                    <th style={headerStyle}>ID</th>
                    <th style={headerStyle}>Solde</th>
                    <th style={headerStyle}>Date</th>
                    <th style={headerStyle}>Type</th>
                    <th style={headerStyle}>Action</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id} style={{ transition: "all 0.2s" }}>
                        <td style={cellStyle}>{compte.id}</td>
                        <td style={cellStyle}>{compte.solde}</td>
                        <td style={cellStyle}>{compte.dateCreation}</td>
                        <td style={cellStyle}>{compte.typeCompte}</td>
                        <td style={cellStyle}>
                            <DeleteCompte id={compte.id} refetch={refetch} />
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

const headerStyle = {
    padding: "10px",
    backgroundColor: "#444",
    color: "#fff",
    textAlign: "center",
    fontFamily: "'Poppins', sans-serif",
};

const cellStyle = {
    padding: "10px",
    textAlign: "center",
    borderBottom: "1px solid #ddd",
    fontFamily: "'Poppins', sans-serif",
};

export default ListComptes;
