import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div className="modal-background flex justify-center items-center fixed top-0 left-0 right-0 bottom-0 z-50">
            <div className="modal-container bg-white p-6 rounded-lg shadow-lg w-80">
                <h2 className="text-xl font-semibold mb-4">Ajouter un Compte</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="flex flex-col">
                        <label className="mb-2">Solde</label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            className="input-box"
                            required
                        />
                    </div>
                    <div className="flex flex-col">
                        <label className="mb-2">Date de Création</label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            className="input-box"
                            required
                        />
                    </div>
                    <div className="flex flex-col">
                        <label className="mb-2">Type de Compte</label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            className="input-box"
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button type="submit" disabled={loading} className="submit-btn">
                        {loading ? 'Ajout en cours...' : 'Ajouter'}
                    </button>
                    {error && <p className="text-red-500">{error.message}</p>}
                </form>
                <button onClick={() => window.location.reload()} className="text-blue-500 mt-4">Voir tous les comptes</button>
            </div>
        </div>
    );
};

export default AddCompte;
