import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div className="modal-background" style={styles.modalBackground}>
            <div style={styles.modalContainer}>
                <h2 style={styles.title}>Ajouter un Compte</h2>
                <form onSubmit={handleSubmit} style={styles.form}>
                    <div style={styles.inputGroup}>
                        <label style={styles.label}>Solde</label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            style={styles.input}
                            required
                        />
                    </div>
                    <div style={styles.inputGroup}>
                        <label style={styles.label}>Date de Création</label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            style={styles.input}
                            required
                        />
                    </div>
                    <div style={styles.inputGroup}>
                        <label style={styles.label}>Type de Compte</label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            style={styles.input}
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button type="submit" disabled={loading} style={styles.submitButton}>
                        {loading ? 'Ajout en cours...' : 'Ajouter'}
                    </button>
                    {error && <p style={styles.errorMessage}>{error.message}</p>}
                </form>
            </div>
        </div>
    );
};

const styles = {
    modalBackground: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        zIndex: 50,
    },
    modalContainer: {
        backgroundColor: 'white',
        padding: '24px',
        borderRadius: '16px',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
        width: '300px',
    },
    title: {
        fontSize: '20px',
        fontWeight: '600',
        marginBottom: '16px',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '16px',
    },
    inputGroup: {
        display: 'flex',
        flexDirection: 'column',
    },
    label: {
        marginBottom: '8px',
        fontWeight: '500',
    },
    input: {
        padding: '12px',
        borderRadius: '8px',
        border: '1px solid #ddd',
        boxSizing: 'border-box',
        marginBottom: '12px',
        fontSize: '16px',
        outline: 'none',
    },
    submitButton: {
        backgroundColor: '#3B82F6',
        color: 'white',
        padding: '12px',
        borderRadius: '8px',
        fontWeight: '500',
        cursor: 'pointer',
    },
    errorMessage: {
        color: 'red',
        marginTop: '8px',
    },
};

export default AddCompte;
