import React from 'react';
import { useMutation, gql } from '@apollo/client';

const DELETE_COMPTE = gql`
  mutation DeleteCompte($id: ID!) {
    deleteCompte(id: $id)
  }
`;

const DeleteComptes = ({ id, refetch }) => {
    const [deleteCompte, { loading }] = useMutation(DELETE_COMPTE, {
        onCompleted: refetch,
    });

    const handleDelete = () => {
        deleteCompte({ variables: { id } });
    };

    return (
        <button
            onClick={handleDelete}
            disabled={loading}
            style={{
                padding: '8px 16px',
                backgroundColor: '#ff4d4f',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                marginLeft: '10px',
                boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
                transition: 'background-color 0.3s ease, transform 0.3s ease'
            }}
            onMouseOver={() => {
                document.getElementById(id).style.transform = 'scale(1.05)';
            }}
            onMouseOut={() => {
                document.getElementById(id).style.transform = 'scale(1)';
            }}
        >
            {loading ? 'Suppression...' : 'Supprimer'}
        </button>
    );
};

export default DeleteComptes;
