import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde("");
            setDateCreation("");
            setTypeCompte("COURANT");
        },
    });

    return (
        <div
            style={{
                background: "#ffffff",
                borderRadius: "15px",
                boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
                padding: "30px",
                margin: "20px",
                maxWidth: "400px",
                animation: "fadeIn 1s ease-in-out",
                fontFamily: "'Roboto', sans-serif",
            }}
        >
            <h2
                style={{
                    fontSize: "1.8em",
                    marginBottom: "20px",
                    textAlign: "center",
                    color: "#2c3e50",
                }}
            >
                Ajouter un Compte
            </h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    saveCompte({
                        variables: {
                            compteInput: {
                                solde: parseFloat(solde),
                                dateCreation,
                                typeCompte,
                            },
                        },
                    });
                }}
                style={{ display: "flex", flexDirection: "column", gap: "15px" }}
            >
                <div>
                    <label style={labelStyle}>Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        style={inputStyle}
                    />
                </div>
                <div>
                    <label style={labelStyle}>Date de Cr√©ation :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        style={inputStyle}
                    />
                </div>
                <div>
                    <label style={labelStyle}>Type de Compte :</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={inputStyle}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Epargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        backgroundColor: "#3498db",
                        color: "white",
                        padding: "10px",
                        borderRadius: "10px",
                        border: "none",
                        fontSize: "1em",
                        cursor: "pointer",
                        transition: "0.3s ease",
                    }}
                >
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && (
                    <p style={{ color: "red", textAlign: "center" }}>
                        Erreur : {error.message}
                    </p>
                )}
            </form>
        </div>
    );
};

const labelStyle = {
    fontSize: "1em",
    marginBottom: "5px",
    color: "#2c3e50",
};

const inputStyle = {
    width: "100%",
    padding: "10px",
    borderRadius: "10px",
    border: "1px solid #ddd",
    outline: "none",
    fontSize: "1em",
    color: "#34495e",
};

export default AddCompte;
