import React, { useState, useRef } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch, showPopup, setShowPopup }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const buttonRef = useRef(null); // Utiliser une référence pour le bouton

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    const closePopup = () => {
        setShowPopup(false); // Close the popup
    };

    const handleMouseOver = () => {
        if (buttonRef.current) {
            buttonRef.current.style.backgroundColor = '#e04343'; // Change color when mouse hovers
        }
    };

    const handleMouseOut = () => {
        if (buttonRef.current) {
            buttonRef.current.style.backgroundColor = '#ff4d4f'; // Reset color when mouse leaves
        }
    };

    return (
        <div
            style={{
                display: showPopup ? 'flex' : 'none',
                justifyContent: 'center',
                alignItems: 'center',
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.6)',
                zIndex: 50,
                animation: 'fadeIn 0.5s forwards',
            }}
        >
            <div
                style={{
                    background: 'linear-gradient(135deg, #7a4dff, #9e8df0)', // Violet gradient
                    padding: '30px',
                    borderRadius: '20px',
                    boxShadow: '0px 10px 30px rgba(0, 0, 0, 0.2)',
                    width: '80%',
                    maxWidth: '500px',
                    animation: 'popupSlide 0.5s forwards',
                    position: 'relative',
                    fontFamily: 'Arial, sans-serif',
                }}
            >
                <button
                    onClick={closePopup}
                    style={{
                        position: 'absolute',
                        top: '10px',
                        right: '10px',
                        backgroundColor: 'transparent',
                        border: 'none',
                        fontSize: '24px',
                        fontWeight: 'bold',
                        color: '#fff',
                        cursor: 'pointer',
                        borderRadius: '50%',
                        padding: '5px',
                    }}
                >
                    X
                </button>
                <h2
                    style={{
                        color: '#fff',
                        fontSize: '26px',
                        fontWeight: 'bold',
                        marginBottom: '20px',
                        textAlign: 'center',
                    }}
                >
                    Ajouter un Compte
                </h2>
                <form
                    onSubmit={handleSubmit}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '15px',
                    }}
                >
                    <div>
                        <label
                            style={{
                                color: '#fff',
                                fontSize: '16px',
                                marginBottom: '8px',
                            }}
                        >
                            Solde :
                        </label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            required
                            style={{
                                padding: '12px',
                                borderRadius: '10px',
                                border: '1px solid #ddd',
                                fontSize: '16px',
                                color: '#333',
                            }}
                        />
                    </div>
                    <div>
                        <label
                            style={{
                                color: '#fff',
                                fontSize: '16px',
                                marginBottom: '8px',
                            }}
                        >
                            Date de Création :
                        </label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            required
                            style={{
                                padding: '12px',
                                borderRadius: '10px',
                                border: '1px solid #ddd',
                                fontSize: '16px',
                                color: '#333',
                            }}
                        />
                    </div>
                    <div>
                        <label
                            style={{
                                color: '#fff',
                                fontSize: '16px',
                                marginBottom: '8px',
                            }}
                        >
                            Type de Compte :
                        </label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            style={{
                                padding: '12px',
                                borderRadius: '10px',
                                border: '1px solid #ddd',
                                fontSize: '16px',
                                color: '#333',
                            }}
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        ref={buttonRef}
                        onMouseOver={handleMouseOver}
                        onMouseOut={handleMouseOut}
                        style={{
                            backgroundColor: '#ff4d4f',
                            color: '#fff',
                            padding: '12px 20px',
                            borderRadius: '30px',
                            fontSize: '16px',
                            cursor: 'pointer',
                            border: 'none',
                            marginTop: '20px',
                            transition: 'all 0.3s ease',
                        }}
                    >
                        {loading ? 'Ajout en cours...' : 'Ajouter'}
                    </button>
                    {error && <p style={{ color: 'red', marginTop: '10px' }}>Erreur : {error.message}</p>}
                </form>
            </div>
        </div>
    );
};

export default AddCompte;
