import React, { useState } from "react";
import { useMutation, gql, useQuery } from "@apollo/client";

// Mutation GraphQL pour ajouter un compte
const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

// Requête GraphQL pour récupérer tous les comptes
const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = () => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const { refetch } = useQuery(GET_ALL_COMPTES); // Pour rafraîchir la liste après l'ajout

    // Mutation pour ajouter un compte
    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch(); // Rafraîchit la liste des comptes après l'ajout
            setSolde("");
            setDateCreation("");
            setTypeCompte("COURANT");
        },
    });

    // Gestion de la soumission du formulaire
    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div className="max-w-md mx-auto mt-10 p-6 bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 rounded-lg shadow-xl transform transition-all duration-300">
            <h2 className="text-3xl font-bold text-white text-center mb-6">Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div className="flex flex-col">
                    <label className="text-white font-semibold mb-2">Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        className="p-3 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-purple-600"
                    />
                </div>
                <div className="flex flex-col">
                    <label className="text-white font-semibold mb-2">Date de Création :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        className="p-3 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-purple-600"
                    />
                </div>
                <div className="flex flex-col">
                    <label className="text-white font-semibold mb-2">Type de Compte :</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        className="p-3 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-purple-600"
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    className="w-full p-3 mt-4 bg-green-600 text-white font-semibold rounded-lg shadow-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 transform transition-all duration-300"
                >
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && <p className="text-red-400 mt-4">Erreur : {error.message}</p>}
            </form>
        </div>
    );
};

export default AddCompte;
