import React, { useState } from 'react';
import AddCompte from './components/AddCompte';
import PopupListComptes from './components/ListComptes';
import { ApolloProvider, InMemoryCache, ApolloClient } from '@apollo/client';

// Configuration de Apollo Client
const client = new ApolloClient({
    uri: 'http://localhost:8084/graphql',
    cache: new InMemoryCache(),
});

const App = () => {
    const [view, setView] = useState('add');
    const [popupVisible, setPopupVisible] = useState(false);
    const refetch = () => {}; // Utilisez cette fonction pour forcer une actualisation de la liste

    return (
        <ApolloProvider client={client}>
            {view === 'add' ? (
                <AddCompte setView={setView} refetch={refetch} />
            ) : (
                <div>
                    <PopupListComptes closePopup={() => setPopupVisible(false)} refetch={refetch} />
                </div>
            )}
        </ApolloProvider>
    );
};

export default App;
