import React, { useState } from "react";
import { useQuery, gql } from "@apollo/client";
import AddCompte from "./AddCompte";
import DeleteCompte from "./DeleteComptes";

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const { loading, error, data, refetch } = useQuery(GET_ALL_COMPTES);

    // Gérer la visibilité du formulaire d'ajout de compte (initialement caché)
    const [isFormVisible, setIsFormVisible] = useState(false);

    // Fonction pour basculer la visibilité du formulaire
    const toggleFormVisibility = () => {
        setIsFormVisible(!isFormVisible);
    };

    if (loading) return <p>Chargement...</p>;
    if (error) return <p style={{ color: "red" }}>Erreur : {error.message}</p>;

    return (
        <div className="p-6 bg-white shadow-md rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Liste des Comptes</h2>

            {/* Bouton pour afficher/masquer le formulaire d'ajout de compte */}
            <button
                onClick={toggleFormVisibility}
                className="bg-blue-500 text-white p-2 rounded-md mb-4 hover:bg-blue-700 transition-all duration-300"
            >
                {isFormVisible ? "Cacher le formulaire" : "Voir Ajouter un Compte"}
            </button>

            {/* Afficher le formulaire d'ajout uniquement si isFormVisible est vrai */}
            {isFormVisible && <AddCompte refetch={refetch} />}

            {/* Affichage de la liste des comptes */}
            <table className="min-w-full bg-white border-collapse shadow-lg mt-4">
                <thead>
                <tr className="border-b bg-gray-100">
                    <th className="py-3 px-6 text-left text-lg">ID</th>
                    <th className="py-3 px-6 text-left text-lg">Solde</th>
                    <th className="py-3 px-6 text-left text-lg">Date de Création</th>
                    <th className="py-3 px-6 text-left text-lg">Type de Compte</th>
                    <th className="py-3 px-6 text-left text-lg">Actions</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id} className="border-b hover:bg-gray-100 transition-all duration-300">
                        <td className="py-3 px-6">{compte.id}</td>
                        <td className="py-3 px-6">{compte.solde}</td>
                        <td className="py-3 px-6">{compte.dateCreation}</td>
                        <td className="py-3 px-6">{compte.typeCompte}</td>
                        <td className="py-3 px-6">
                            {/* Passer refetch pour rafraîchir la liste après suppression */}
                            <DeleteCompte id={compte.id} refetch={refetch} />
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListComptes;
