import React from "react";
import { useQuery, gql } from "@apollo/client";

// GraphQL Query pour récupérer tous les comptes
const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ComptesList = ({ onDeleteClick }) => {
    const { loading, error, data } = useQuery(GET_ALL_COMPTES);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p>Erreur : {error.message}</p>;

    return (
        <div style={containerStyle}>
            <h2>Liste des Comptes</h2>
            <table style={tableStyle}>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Solde</th>
                    <th>Date de Création</th>
                    <th>Type de Compte</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id}>
                        <td>{compte.id}</td>
                        <td>{compte.solde}</td>
                        <td>{compte.dateCreation}</td>
                        <td>{compte.typeCompte}</td>
                        <td>
                            <button
                                onClick={() => onDeleteClick(compte.id)}
                                style={actionButtonStyle}
                            >
                                Supprimer
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

// Styles CSS pour ce composant
const containerStyle = {
    padding: "20px",
};

const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "20px",
    borderRadius: "8px",
};

const actionButtonStyle = {
    padding: "8px 12px",
    backgroundColor: "#E74C3C",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
};

export default ComptesList;
