import React, { useState } from "react";
import { useQuery, gql } from "@apollo/client";
import AddCompte from "./AddCompte"; // Import du composant AddCompte

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const { loading, error, data } = useQuery(GET_ALL_COMPTES);
    const [isPopupVisible, setPopupVisible] = useState(false);

    // Fonction togglePopup pour afficher/masquer la popup
    const togglePopup = () => {
        setPopupVisible(!isPopupVisible);
    };

    if (loading) return <p>Chargement...</p>;
    if (error) return <p>Erreur : {error.message}</p>;

    return (
        <div style={{ padding: "20px", background: "#f9f9f9", borderRadius: "10px" }}>
            <h2>Liste des Comptes</h2>
            <button
                onClick={togglePopup}
                style={{
                    padding: "10px 20px",
                    backgroundColor: "#007bff",
                    color: "white",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    transition: "0.3s",
                }}
            >
                Ajouter un Compte
            </button>

            {/* Affichage de la liste des comptes */}
            <table style={{ width: "100%", marginTop: "20px", borderCollapse: "collapse" }}>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Solde</th>
                    <th>Date de Cr√©ation</th>
                    <th>Type de Compte</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id}>
                        <td>{compte.id}</td>
                        <td>{compte.solde}</td>
                        <td>{compte.dateCreation}</td>
                        <td>{compte.typeCompte}</td>
                    </tr>
                ))}
                </tbody>
            </table>

            {/* Afficher la popup si isPopupVisible est vrai */}
            {isPopupVisible && <AddCompte togglePopup={togglePopup} />}
        </div>
    );
};

export default ListComptes;
