import React, { useState } from "react";
import { useQuery, gql } from "@apollo/client";
import AddCompte from "./AddCompte";
import DeleteComptes from "./DeleteComptes";

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const { loading, error, data, refetch } = useQuery(GET_ALL_COMPTES);
    const [showPopup, setShowPopup] = useState(false);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p style={{ color: 'red' }}>Erreur : {error.message}</p>;

    const handleShowPopup = () => {
        setShowPopup(true); // Show the popup when clicking the button
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '20px' }}>
                Liste des Comptes
            </h2>
            <button
                onClick={handleShowPopup}
                style={{
                    backgroundColor: '#007bff',
                    color: 'white',
                    padding: '12px 20px',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginBottom: '20px',
                }}
            >
                Ajouter un Compte
            </button>
            <AddCompte refetch={refetch} showPopup={showPopup} setShowPopup={setShowPopup} />
            <table style={{
                width: '100%',
                borderCollapse: 'collapse',
                marginTop: '20px',
                boxShadow: '0px 6px 12px rgba(0, 0, 0, 0.1)',
            }}>
                <thead>
                <tr>
                    <th style={{ padding: '10px', border: '1px solid #ddd' }}>ID</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd' }}>Solde</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd' }}>Date de Création</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd' }}>Type de Compte</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd' }}>Actions</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id} style={{ textAlign: 'center' }}>
                        <td style={{ padding: '10px', border: '1px solid #ddd' }}>{compte.id}</td>
                        <td style={{ padding: '10px', border: '1px solid #ddd' }}>{compte.solde}</td>
                        <td style={{ padding: '10px', border: '1px solid #ddd' }}>{compte.dateCreation}</td>
                        <td style={{ padding: '10px', border: '1px solid #ddd' }}>{compte.typeCompte}</td>
                        <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                            <DeleteComptes id={compte.id} refetch={refetch} />
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListComptes;
