import React from "react";
import { useQuery, gql } from "@apollo/client";
import { FaPiggyBank } from "react-icons/fa";

const GET_COMPTES = gql`
  query {
    allComptes {
      id
      solde
      type
      dateCreation
    }
  }
`;

function Comptes() {
    const { loading, error, data } = useQuery(GET_COMPTES);

    if (loading)
        return <p className="text-center text-primary">Chargement des données...</p>;
    if (error) return <p className="text-red-500">Erreur : {error.message}</p>;

    return (
        <div className="max-w-4xl mx-auto mt-10">
            <h2 className="text-3xl font-bold text-primary mb-6 flex items-center gap-2">
                <FaPiggyBank className="text-accent" />
                Liste des Comptes
            </h2>
            <ul className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {data.allComptes.map((compte) => (
                    <li
                        key={compte.id}
                        className="p-4 bg-white shadow-md rounded-lg hover:shadow-xl transform transition-transform duration-200 hover:scale-105"
                    >
                        <h3 className="text-xl font-semibold text-gray-800">
                            {compte.type}
                        </h3>
                        <p className="text-gray-500">Solde : {compte.solde} DH</p>
                        <p className="text-gray-400 text-sm">
                            Créé le : {compte.dateCreation}
                        </p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Comptes;
