import React, { useState } from "react";
import { gql, useMutation } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation ($compte: CompteInput!) {
    saveCompteInput(compte: $compte) {
      id
      solde
      type
      dateCreation
    }
  }
`;

function AddCompte() {
    const [formData, setFormData] = useState({ solde: "", dateCreation: "", type: "COURANT" });

    const [addCompte] = useMutation(ADD_COMPTE, {
        onCompleted: (data) => {
            console.log("Compte ajouté :", data);
            alert("Compte ajouté avec succès !");
        },
        onError: (error) => {
            console.error("Erreur lors de l'ajout :", error);
            alert(`Erreur : ${error.message}`);
        },
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({ variables: { compte: { ...formData, solde: parseFloat(formData.solde) } } });
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Solde:
                <input name="solde" type="number" value={formData.solde} onChange={handleChange} />
            </label>
            <label>
                Date de création:
                <input name="dateCreation" type="date" value={formData.dateCreation} onChange={handleChange} />
            </label>
            <label>
                Type:
                <select name="type" value={formData.type} onChange={handleChange}>
                    <option value="COURANT">Courant</option>
                    <option value="EPARGNE">Épargne</option>
                </select>
            </label>
            <button type="submit">Ajouter</button>
        </form>
    );
}

export default AddCompte;
