import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";
import { toast } from "react-toastify";

const ADD_COMPTE = gql`
  mutation ($compte: CompteInput!) {
    saveCompte(compte: $compte) {
      id
      solde
      type
    }
  }
`;

function AddCompte() {
    const [solde, setSolde] = useState("");
    const [type, setType] = useState("COURANT");
    const [addCompte] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            toast.success("Compte ajouté avec succès !");
            setSolde("");
        },
        onError: (error) => {
            toast.error(`Erreur : ${error.message}`);
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({
            variables: { compte: { solde: parseFloat(solde), type } },
        });
    };

    return (
        <div className="max-w-xl mx-auto bg-gradient-to-r from-primary to-accent text-white p-8 rounded-lg shadow-lg mt-10">
            <h2 className="text-3xl font-bold mb-6">Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                    <label className="block text-sm font-medium">Solde (DH)</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        className="w-full mt-2 p-2 border border-gray-300 rounded-md shadow-sm text-gray-900"
                        required
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium">Type de Compte</label>
                    <select
                        value={type}
                        onChange={(e) => setType(e.target.value)}
                        className="w-full mt-2 p-2 border border-gray-300 rounded-md shadow-sm text-gray-900"
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    className="w-full py-2 px-4 bg-white text-primary font-bold rounded hover:bg-gray-100 transition-colors"
                >
                    Ajouter le Compte
                </button>
            </form>
        </div>
    );
}

export default AddCompte;
