import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation ($compte: CompteInput!) {
    saveCompte(compte: $compte) {
      id
      solde
      type
    }
  }
`;

const styles = {
    container: {
        maxWidth: "600px",
        margin: "30px auto",
        background: "linear-gradient(to right, #4f46e5, #f43f5e)",
        color: "white",
        padding: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
        fontFamily: "'Arial', sans-serif",
    },
    title: {
        fontSize: "28px",
        fontWeight: "bold",
        marginBottom: "20px",
    },
    formGroup: {
        marginBottom: "20px",
    },
    label: {
        display: "block",
        marginBottom: "5px",
        fontSize: "14px",
        fontWeight: "bold",
    },
    input: {
        width: "100%",
        padding: "10px",
        borderRadius: "5px",
        border: "1px solid #ddd",
        fontSize: "16px",
        marginTop: "5px",
    },
    button: {
        width: "100%",
        padding: "10px",
        backgroundColor: "#fff",
        color: "#4f46e5",
        fontWeight: "bold",
        fontSize: "16px",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
        transition: "background-color 0.3s, color 0.3s",
    },
    buttonHover: {
        backgroundColor: "#4f46e5",
        color: "#fff",
    },
};

function AddCompte() {
    const [solde, setSolde] = useState("");
    const [type, setType] = useState("COURANT");
    const [addCompte] = useMutation(ADD_COMPTE);

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({ variables: { compte: { solde: parseFloat(solde), type } } });
        setSolde("");
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit}>
                <div style={styles.formGroup}>
                    <label style={styles.label}>Solde (DH)</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        style={styles.input}
                        required
                    />
                </div>
                <div style={styles.formGroup}>
                    <label style={styles.label}>Type de Compte</label>
                    <select
                        value={type}
                        onChange={(e) => setType(e.target.value)}
                        style={styles.input}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Ã‰pargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    style={styles.button}
                    onMouseEnter={(e) =>
                        (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)
                    }
                    onMouseLeave={(e) => (e.target.style.backgroundColor = "#fff")}
                >
                    Ajouter le Compte
                </button>
            </form>
        </div>
    );
}

export default AddCompte;
