import React, { useState } from "react";
import { useMutation, gql, useQuery } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = () => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const { refetch } = useQuery(GET_ALL_COMPTES); // Utilise useQuery pour obtenir refetch

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch(); // Rafraîchit la liste des comptes après l'ajout
            setSolde("");
            setDateCreation("");
            setTypeCompte("COURANT");
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div style={{ padding: "20px", border: "1px solid #ddd", borderRadius: "10px", margin: "20px" }}>
            <h2>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
                <div>
                    <label>Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Date de Création :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Type de Compte :</label>
                    <select value={typeCompte} onChange={(e) => setTypeCompte(e.target.value)}>
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && <p style={{ color: "red" }}>Erreur : {error.message}</p>}
            </form>
        </div>
    );
};

export default AddCompte;
