import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div style={styles.formContainer}>
            <h2 style={styles.title}>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.inputGroup}>
                    <label style={styles.label}>Solde</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        style={styles.input}
                        required
                    />
                </div>
                <div style={styles.inputGroup}>
                    <label style={styles.label}>Date de Création</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        style={styles.input}
                        required
                    />
                </div>
                <div style={styles.inputGroup}>
                    <label style={styles.label}>Type de Compte</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={styles.input}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button type="submit" disabled={loading} style={styles.submitButton}>
                    {loading ? 'Ajout en cours...' : 'Ajouter'}
                </button>
                {error && <p style={styles.errorMessage}>{error.message}</p>}
            </form>
        </div>
    );
};

const styles = {
    formContainer: {
        width: '100%',
        padding: '20px',
        marginBottom: '20px',
        background: 'linear-gradient(135deg, #6a0dad, #ff7f00)',
        borderRadius: '12px',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
    },
    title: {
        fontSize: '24px',
        fontWeight: '700',
        color: 'white',
        textAlign: 'center',
        marginBottom: '20px',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '16px',
    },
    inputGroup: {
        display: 'flex',
        flexDirection: 'column',
    },
    label: {
        color: 'white',
        marginBottom: '8px',
    },
    input: {
        padding: '12px',
        borderRadius: '8px',
        border: '1px solid #ddd',
        marginBottom: '12px',
        fontSize: '16px',
        outline: 'none',
        fontWeight: '400',
    },
    submitButton: {
        backgroundColor: '#ff7f00',
        color: 'white',
        padding: '12px',
        borderRadius: '8px',
        fontWeight: '500',
        cursor: 'pointer',
    },
    errorMessage: {
        color: 'red',
        marginTop: '8px',
    },
};

export default AddCompte;
