import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde("");
            setDateCreation("");
            setTypeCompte("COURANT");
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Ajouter un Compte</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.field}>
                    <label style={styles.label}>Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        style={styles.input}
                    />
                </div>
                <div style={styles.field}>
                    <label style={styles.label}>Date de Création :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        style={styles.input}
                    />
                </div>
                <div style={styles.field}>
                    <label style={styles.label}>Type de Compte :</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={styles.select}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button type="submit" disabled={loading} style={styles.button}>
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && <p style={styles.error}>Erreur : {error.message}</p>}
            </form>
        </div>
    );
};

const styles = {
    container: {
        backgroundColor: "#f9f9f9",
        padding: "20px",
        borderRadius: "12px",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        margin: "20px auto",
        maxWidth: "400px",
        animation: "fadeIn 0.8s ease",
    },
    title: {
        textAlign: "center",
        color: "#333",
        fontFamily: "'Poppins', sans-serif",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
    },
    field: {
        display: "flex",
        flexDirection: "column",
    },
    label: {
        fontWeight: "bold",
        marginBottom: "5px",
        color: "#555",
    },
    input: {
        padding: "10px",
        border: "1px solid #ddd",
        borderRadius: "8px",
        fontSize: "16px",
        outline: "none",
        transition: "box-shadow 0.3s",
    },
    select: {
        padding: "10px",
        border: "1px solid #ddd",
        borderRadius: "8px",
        fontSize: "16px",
        outline: "none",
        transition: "box-shadow 0.3s",
    },
    button: {
        backgroundColor: "#4CAF50",
        color: "#fff",
        padding: "12px 20px",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
        transition: "background-color 0.3s",
        fontFamily: "'Poppins', sans-serif",
    },
    error: {
        color: "red",
        fontWeight: "bold",
        textAlign: "center",
    },
};

export default AddCompte;
