import React from "react";
import { useQuery, gql } from "@apollo/client";
import DeleteCompte from "./DeleteComptes";

const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const ListComptes = () => {
    const { loading, error, data, refetch } = useQuery(GET_ALL_COMPTES);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p style={{ color: "red" }}>Erreur : {error.message}</p>;

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Liste des Comptes</h2>
            <table style={styles.table}>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Solde</th>
                    <th>Date de Cr√©ation</th>
                    <th>Type de Compte</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {data.allComptes.map((compte) => (
                    <tr key={compte.id} style={styles.row}>
                        <td>{compte.id}</td>
                        <td>{compte.solde}</td>
                        <td>{compte.dateCreation}</td>
                        <td>{compte.typeCompte}</td>
                        <td>
                            <DeleteCompte id={compte.id} refetch={refetch} />
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

const styles = {
    container: {
        padding: "20px",
        backgroundColor: "#fdfdfd",
        borderRadius: "12px",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        animation: "fadeIn 0.8s ease",
    },
    title: {
        textAlign: "center",
        color: "#333",
        fontFamily: "'Poppins', sans-serif",
    },
    table: {
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "15px",
    },
    row: {
        transition: "background-color 0.3s",
    },
};

export default ListComptes;
