import React, { useState } from "react";
import { useQuery, useMutation, gql } from "@apollo/client";

const GET_COMPTES = gql`
  query {
    allComptes {
      id
      solde
      type
      dateCreation
    }
  }
`;

const DELETE_COMPTE = gql`
  mutation ($id: Long!) {
    deleteCompte(id: $id)
  }
`;

const ADD_COMPTE = gql`
  mutation ($solde: Float!, $dateCreation: String!, $type: TypeCompte!) {
    saveCompteInput(compte: { solde: $solde, dateCreation: $dateCreation, type: $type }) {
      id
      solde
      type
      dateCreation
    }
  }
`;


const ComptesPopup = ({ isOpen, onClose }) => {
    const { loading, error, data } = useQuery(GET_COMPTES);
    const [deleteCompte] = useMutation(DELETE_COMPTE, {
        refetchQueries: [{ query: GET_COMPTES }],
    });
    const [addCompte] = useMutation(ADD_COMPTE, {
        refetchQueries: [{ query: GET_COMPTES }],
    });

    const [formData, setFormData] = useState({
        solde: "",
        dateCreation: "",
        type: "COURANT", // Valeur par défaut
    });

    const handleDelete = (id) => {
        deleteCompte({ variables: { id } })
            .then(() => {
                alert(`Compte ${id} supprimé avec succès !`);
            })
            .catch((err) => {
                alert(`Erreur : ${err.message}`);
            });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addCompte({
            variables: {
                solde: parseFloat(formData.solde),
                dateCreation: formData.dateCreation,
                type: formData.type,
            },
        })
            .then(() => {
                alert("Compte ajouté avec succès !");
                setFormData({ solde: "", dateCreation: "", type: "COURANT" }); // Réinitialiser le formulaire
            })
            .catch((err) => {
                alert(`Erreur : ${err.message}`);
            });
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    if (!isOpen) return null;

    return (
        <div
            style={{
                position: "fixed",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
                background: "white",
                padding: "20px",
                borderRadius: "10px",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
                zIndex: 1000,
                width: "80%",
                maxHeight: "80vh",
                overflowY: "auto",
            }}
        >
            <button
                onClick={onClose}
                style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    background: "red",
                    color: "white",
                    border: "none",
                    padding: "5px 10px",
                    borderRadius: "5px",
                    cursor: "pointer",
                }}
            >
                Fermer
            </button>
            <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Liste des Comptes</h2>
            {loading && <p>Chargement...</p>}
            {error && <p>Erreur : {error.message}</p>}
            {data && (
                <ul
                    style={{
                        listStyle: "none",
                        padding: 0,
                        margin: 0,
                        maxHeight: "60vh",
                        overflowY: "auto",
                        border: "1px solid #ddd",
                        borderRadius: "5px",
                    }}
                >
                    {data.allComptes.map((compte) => (
                        <li
                            key={compte.id}
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                padding: "10px",
                                borderBottom: "1px solid #ddd",
                            }}
                        >
              <span>
                <strong>{compte.type}</strong> - {compte.solde.toFixed(2)} DH (Créé le{" "}
                  {compte.dateCreation})
              </span>
                            <button
                                onClick={() => handleDelete(compte.id)}
                                style={{
                                    background: "red",
                                    color: "white",
                                    border: "none",
                                    padding: "5px 10px",
                                    borderRadius: "5px",
                                    cursor: "pointer",
                                }}
                            >
                                Supprimer
                            </button>
                        </li>
                    ))}
                </ul>
            )}
            <h3 style={{ textAlign: "center", marginTop: "20px" }}>Ajouter un Compte</h3>
            <form
                onSubmit={handleSubmit}
                style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "10px",
                    marginTop: "10px",
                }}
            >
                <input
                    type="number"
                    name="solde"
                    placeholder="Solde"
                    value={formData.solde}
                    onChange={handleChange}
                    required
                    style={{
                        padding: "10px",
                        border: "1px solid #ddd",
                        borderRadius: "5px",
                    }}
                />
                <input
                    type="date"
                    name="dateCreation"
                    placeholder="Date de Création"
                    value={formData.dateCreation}
                    onChange={handleChange}
                    required
                    style={{
                        padding: "10px",
                        border: "1px solid #ddd",
                        borderRadius: "5px",
                    }}
                />
                <select
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    style={{
                        padding: "10px",
                        border: "1px solid #ddd",
                        borderRadius: "5px",
                    }}
                >
                    <option value="COURANT">Courant</option>
                    <option value="EPARGNE">Épargne</option>
                </select>
                <button
                    type="submit"
                    style={{
                        background: "green",
                        color: "white",
                        border: "none",
                        padding: "10px",
                        borderRadius: "5px",
                        cursor: "pointer",
                    }}
                >
                    Ajouter
                </button>
            </form>
        </div>
    );
};

export default ComptesPopup;
