import React from "react";
import { useQuery, gql } from "@apollo/client";
import { FaPiggyBank } from "react-icons/fa";

const GET_COMPTES = gql`
  query {
    allComptes {
      id
      solde
      type
      dateCreation
    }
  }
`;

function Comptes() {
    const { loading, error, data } = useQuery(GET_COMPTES);

    if (loading)
        return (
            <p className="text-center text-primary animate-pulse">Chargement...</p>
        );
    if (error)
        return <p className="text-red-500 text-center">Erreur : {error.message}</p>;

    return (
        <div className="max-w-5xl mx-auto p-6">
            <h2 className="text-4xl font-bold text-primary mb-8 flex items-center gap-4">
                <FaPiggyBank className="text-accent text-4xl" />
                Liste des Comptes
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {data.allComptes.map((compte) => (
                    <div
                        key={compte.id}
                        className="bg-white shadow-lg rounded-lg p-6 hover:shadow-2xl hover:scale-105 transition-transform duration-300"
                    >
                        <h3 className="text-xl font-bold text-gray-800 uppercase">
                            {compte.type}
                        </h3>
                        <p className="text-lg text-gray-600 font-semibold">
                            Solde : {compte.solde.toFixed(2)} DH
                        </p>
                        <p className="text-sm text-gray-400 mt-2">
                            Créé le : {compte.dateCreation}
                        </p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Comptes;
