import React from 'react';
import { useQuery, gql } from '@apollo/client';
import ComptesPopup from './ComptesPopup';

export const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

function Comptes() {
    const { loading, error, data } = useQuery(ALL_COMPTES);

    if (loading) return <p>Chargement...</p>;
    if (error) return <p>Erreur : {error.message}</p>;

    return (
        <div>
            <h2>Liste des Comptes</h2>
            <ul>
                {data.allComptes.map((compte) => (
                    <li key={compte.id}>
                        {compte.id} - {compte.solde}â‚¬ - {compte.dateCreation} - {compte.typeCompte}
                        <ComptesPopup compte={compte} />
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Comptes;
