import React, { useState } from "react";
import { gql, useMutation } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation ($compte: CompteInput!) {
    saveCompteInput(compte: $compte) {
      id
      solde
      type
      dateCreation
    }
  }
`;

function AddCompteForm() {
    const [formData, setFormData] = useState({
        solde: "",
        dateCreation: "",
        type: "COURANT", // Valeur par défaut
    });

    const [addCompte] = useMutation(ADD_COMPTE, {
        onCompleted: (data) => {
            console.log("Compte ajouté avec succès : ", data);
            alert("Compte ajouté avec succès !");
            // Réinitialiser le formulaire après le succès
            setFormData({
                solde: "",
                dateCreation: "",
                type: "COURANT",
            });
        },
        onError: (error) => {
            console.error("Erreur lors de l'ajout du compte :", error);
            alert(`Erreur : ${error.message}`);
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        // Vérifier que les champs obligatoires sont remplis
        if (!formData.solde || !formData.dateCreation) {
            alert("Veuillez remplir tous les champs !");
            return;
        }

        // Appeler la mutation GraphQL
        addCompte({
            variables: {
                compte: {
                    solde: parseFloat(formData.solde), // Convertir le solde en Float
                    dateCreation: formData.dateCreation, // Date sous forme de string (YYYY-MM-DD)
                    type: formData.type,
                },
            },
        });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    return (
        <form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
            <div style={{ marginBottom: "15px" }}>
                <label htmlFor="solde">Solde</label>
                <input
                    type="number"
                    id="solde"
                    name="solde"
                    value={formData.solde}
                    onChange={handleChange}
                    required
                />
            </div>
            <div style={{ marginBottom: "15px" }}>
                <label htmlFor="dateCreation">Date de création</label>
                <input
                    type="date"
                    id="dateCreation"
                    name="dateCreation"
                    value={formData.dateCreation}
                    onChange={handleChange}
                    required
                />
            </div>
            <div style={{ marginBottom: "15px" }}>
                <label htmlFor="type">Type de compte</label>
                <select
                    id="type"
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                >
                    <option value="COURANT">Courant</option>
                    <option value="EPARGNE">Épargne</option>
                </select>
            </div>
            <button type="submit">Ajouter</button>
        </form>
    );
}

export default AddCompteForm;
