import React, { useState } from "react";
import { ApolloProvider } from "@apollo/client";
import client from "./ApolloClient";
import AddCompte from "./components/AddCompte";
import ComptesPopup from "./components/ComptesPopup";

function App() {
    const [isPopupOpen, setIsPopupOpen] = useState(false);

    const openPopup = () => setIsPopupOpen(true);
    const closePopup = () => setIsPopupOpen(false);

    return (
        <ApolloProvider client={client}>
            <div style={{ fontFamily: "Arial, sans-serif" }}>
                <header
                    style={{
                        background: "linear-gradient(to right, #4f46e5, #f43f5e)",
                        color: "white",
                        padding: "20px",
                        textAlign: "center",
                        fontSize: "24px",
                        fontWeight: "bold",
                    }}
                >
                    Gestion des Comptes Bancaires
                </header>
                <main style={{ padding: "20px" }}>
                    <AddCompte />
                    <button
                        onClick={openPopup}
                        style={{
                            display: "block",
                            margin: "20px auto",
                            background: "#4f46e5",
                            color: "white",
                            border: "none",
                            padding: "10px 20px",
                            borderRadius: "5px",
                            cursor: "pointer",
                            fontSize: "16px",
                        }}
                    >
                        Voir la Liste des Comptes
                    </button>
                    <ComptesPopup isOpen={isPopupOpen} onClose={closePopup} />
                </main>
            </div>
        </ApolloProvider>
    );
}

export default App;
