import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

// Mutation pour ajouter un compte
const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ setShowAddForm }) => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");

    const [saveCompte] = useMutation(ADD_COMPTE, {
        refetchQueries: [{ query: GET_ALL_COMPTES }],
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
        setSolde("");
        setDateCreation("");
        setTypeCompte("COURANT");
        setShowAddForm(false);
    };

    return (
        <div style={popupOverlayStyle}>
            <div style={popupStyle}>
                <h3>Ajouter un Compte</h3>
                <form onSubmit={handleSubmit}>
                    <label>Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        style={inputStyle}
                    />
                    <br />
                    <label>Date de Cr√©ation :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        style={inputStyle}
                    />
                    <br />
                    <label>Type de Compte :</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={selectStyle}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Epargne</option>
                    </select>
                    <br />
                    <button type="submit" style={addButtonStyle}>Ajouter</button>
                </form>
            </div>
        </div>
    );
};

// Styles CSS pour ce composant
const popupOverlayStyle = {
    position: "fixed",
    top: "0",
    left: "0",
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: "1000",
};

const popupStyle = {
    backgroundColor: "#ffffff",
    padding: "20px",
    borderRadius: "10px",
    textAlign: "center",
    width: "300px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
};

const inputStyle = {
    padding: "8px 12px",
    margin: "10px 0",
    width: "100%",
    borderRadius: "5px",
    border: "1px solid #ccc",
};

const selectStyle = {
    padding: "8px 12px",
    margin: "10px 0",
    width: "100%",
    borderRadius: "5px",
    border: "1px solid #ccc",
};

const addButtonStyle = {
    padding: "10px 20px",
    backgroundColor: "#3498db",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
};

export default AddCompte;
