import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

// GraphQL Mutation pour supprimer un compte
const DELETE_COMPTE = gql`
  mutation DeleteCompte($id: ID!) {
    deleteCompte(id: $id)
  }
`;

const DeleteComptes = ({ compteId, onClose }) => {
    const [deleteCompte] = useMutation(DELETE_COMPTE, {
        refetchQueries: [{ query: GET_ALL_COMPTES }],
    });

    const handleDelete = () => {
        deleteCompte({ variables: { id: compteId } });
        onClose();
    };

    const handleCancel = () => {
        onClose();
    };

    return (
        <div style={popupOverlayStyle}>
            <div style={popupStyle}>
                <h3>Confirmation de suppression</h3>
                <p>Êtes-vous sûr de vouloir supprimer ce compte ? Cette action est irréversible.</p>
                <div style={buttonContainerStyle}>
                    <button onClick={handleCancel} style={cancelButtonStyle}>
                        Annuler
                    </button>
                    <button onClick={handleDelete} style={deleteButtonStyle}>
                        Supprimer
                    </button>
                </div>
            </div>
        </div>
    );
};

// Styles CSS pour ce composant
const popupOverlayStyle = {
    position: "fixed",
    top: "0",
    left: "0",
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: "1000",
};

const popupStyle = {
    backgroundColor: "#ffffff",
    padding: "20px",
    borderRadius: "10px",
    textAlign: "center",
    width: "300px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
};

const buttonContainerStyle = {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "20px",
};

const cancelButtonStyle = {
    padding: "10px 20px",
    backgroundColor: "#bdc3c7",
    color: "black",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
};

const deleteButtonStyle = {
    padding: "10px 20px",
    backgroundColor: "#E74C3C",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
};

export default DeleteComptes;
