import React from "react";
import { useQuery, useMutation, gql } from "@apollo/client";

const GET_COMPTES = gql`
  query {
    allComptes {
      id
      solde
      type
      dateCreation
    }
  }
`;

const DELETE_COMPTE = gql`
  mutation ($id: Long!) {
    deleteCompte(id: $id)
  }
`;

const ComptesPopup = ({ isOpen, onClose }) => {
    const { loading, error, data } = useQuery(GET_COMPTES);
    const [deleteCompte] = useMutation(DELETE_COMPTE, {
        refetchQueries: [{ query: GET_COMPTES }],
    });

    const handleDelete = (id) => {
        deleteCompte({ variables: { id } })
            .then(() => {
                alert(`Compte ${id} supprimé avec succès !`);
            })
            .catch((err) => {
                alert(`Erreur : ${err.message}`);
            });
    };

    if (!isOpen) return null;

    return (
        <div
            style={{
                position: "fixed",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
                background: "white",
                padding: "20px",
                borderRadius: "10px",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
                zIndex: 1000,
                width: "80%",
            }}
        >
            <button
                onClick={onClose}
                style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    background: "red",
                    color: "white",
                    border: "none",
                    padding: "5px 10px",
                    borderRadius: "5px",
                    cursor: "pointer",
                }}
            >
                Fermer
            </button>
            <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Liste des Comptes</h2>
            {loading && <p>Chargement...</p>}
            {error && <p>Erreur : {error.message}</p>}
            {data && (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {data.allComptes.map((compte) => (
                        <li
                            key={compte.id}
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                padding: "10px",
                                borderBottom: "1px solid #ddd",
                            }}
                        >
              <span>
                <strong>{compte.type}</strong> - {compte.solde.toFixed(2)} DH
                (Créé le {compte.dateCreation})
              </span>
                            <button
                                onClick={() => handleDelete(compte.id)}
                                style={{
                                    background: "red",
                                    color: "white",
                                    border: "none",
                                    padding: "5px 10px",
                                    borderRadius: "5px",
                                    cursor: "pointer",
                                }}
                            >
                                Supprimer
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ComptesPopup;
