import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = () => {
    const [solde, setSolde] = useState("");
    const [dateCreation, setDateCreation] = useState("");
    const [typeCompte, setTypeCompte] = useState("COURANT");
    const [popup, setPopup] = useState(false);

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        refetchQueries: ["GetAllComptes"],
        onCompleted: () => setPopup(true),
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
        setSolde("");
        setDateCreation("");
        setTypeCompte("COURANT");
    };

    return (
        <div
            style={{
                padding: "20px",
                background: "linear-gradient(to right, #6a11cb, #2575fc)",
                borderRadius: "10px",
                boxShadow: "0 4px 15px rgba(0, 0, 0, 0.3)",
                color: "#fff",
                maxWidth: "500px",
                margin: "30px auto",
            }}
        >
            <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "20px" }}>
                Ajouter un Compte
            </h2>
            <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
                <div>
                    <label style={{ display: "block", marginBottom: "5px" }}>Solde :</label>
                    <input
                        type="number"
                        value={solde}
                        onChange={(e) => setSolde(e.target.value)}
                        required
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "5px",
                            border: "1px solid #ddd",
                            boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
                        }}
                    />
                </div>
                <div>
                    <label style={{ display: "block", marginBottom: "5px" }}>Date de Création :</label>
                    <input
                        type="date"
                        value={dateCreation}
                        onChange={(e) => setDateCreation(e.target.value)}
                        required
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "5px",
                            border: "1px solid #ddd",
                            boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
                        }}
                    />
                </div>
                <div>
                    <label style={{ display: "block", marginBottom: "5px" }}>Type de Compte :</label>
                    <select
                        value={typeCompte}
                        onChange={(e) => setTypeCompte(e.target.value)}
                        style={{
                            width: "100%",
                            padding: "10px",
                            borderRadius: "5px",
                            border: "1px solid #ddd",
                            boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
                        }}
                    >
                        <option value="COURANT">Courant</option>
                        <option value="EPARGNE">Épargne</option>
                    </select>
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: "100%",
                        padding: "10px",
                        borderRadius: "5px",
                        backgroundColor: "#fff",
                        color: "#2575fc",
                        fontWeight: "bold",
                        cursor: "pointer",
                        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
                        transition: "transform 0.2s, box-shadow 0.2s",
                    }}
                    onMouseOver={(e) => {
                        e.target.style.transform = "scale(1.05)";
                        e.target.style.boxShadow = "0 6px 15px rgba(0, 0, 0, 0.3)";
                    }}
                    onMouseOut={(e) => {
                        e.target.style.transform = "scale(1)";
                        e.target.style.boxShadow = "0 4px 10px rgba(0, 0, 0, 0.2)";
                    }}
                >
                    {loading ? "Ajout en cours..." : "Ajouter"}
                </button>
                {error && <p style={{ color: "#ff4d4f", marginTop: "10px" }}>Erreur : {error.message}</p>}
            </form>

            {/* Popup Success */}
            {popup && (
                <div
                    style={{
                        position: "fixed",
                        top: "0",
                        left: "0",
                        width: "100%",
                        height: "100%",
                        backgroundColor: "rgba(0, 0, 0, 0.5)",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                    }}
                    onClick={() => setPopup(false)}
                >
                    <div
                        style={{
                            background: "#fff",
                            padding: "20px",
                            borderRadius: "10px",
                            boxShadow: "0 4px 15px rgba(0, 0, 0, 0.3)",
                            textAlign: "center",
                            animation: "fadeIn 0.3s ease",
                        }}
                    >
                        <h3 style={{ color: "#28a745", fontSize: "18px", fontWeight: "bold" }}>
                            Compte ajouté avec succès !
                        </h3>
                        <p style={{ color: "#555", marginTop: "10px" }}>
                            Le compte a été ajouté avec succès dans la base de données.
                        </p>
                        <button
                            onClick={() => setPopup(false)}
                            style={{
                                marginTop: "20px",
                                padding: "10px 20px",
                                borderRadius: "5px",
                                backgroundColor: "#2575fc",
                                color: "#fff",
                                fontWeight: "bold",
                                cursor: "pointer",
                            }}
                        >
                            OK
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AddCompte;
