import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_COMPTE = gql`
  mutation AddCompte($compteInput: CompteInput!) {
    saveCompte(compteInput: $compteInput) {
      id
      solde
      dateCreation
      typeCompte
    }
  }
`;

const AddCompte = ({ refetch }) => {
    const [solde, setSolde] = useState('');
    const [dateCreation, setDateCreation] = useState('');
    const [typeCompte, setTypeCompte] = useState('COURANT');

    const [saveCompte, { loading, error }] = useMutation(ADD_COMPTE, {
        onCompleted: () => {
            refetch();
            setSolde('');
            setDateCreation('');
            setTypeCompte('COURANT');
        },
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        saveCompte({
            variables: {
                compteInput: {
                    solde: parseFloat(solde),
                    dateCreation,
                    typeCompte,
                },
            },
        });
    };

    return (
        <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 50
        }}>
            <div style={{
                backgroundColor: 'white',
                padding: '20px',
                borderRadius: '10px',
                boxShadow: '0px 10px 30px rgba(0, 0, 0, 0.1)',
                width: '80%',
                maxWidth: '400px',
                animation: 'page-turn 0.5s forwards'
            }}>
                <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '16px' }}>Ajouter un Compte</h2>
                <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                    <div style={{ display: 'flex', flexDirection: 'column' }}>
                        <label>Solde :</label>
                        <input
                            type="number"
                            value={solde}
                            onChange={(e) => setSolde(e.target.value)}
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px'
                            }}
                            required
                        />
                    </div>
                    <div style={{ display: 'flex', flexDirection: 'column' }}>
                        <label>Date de Création :</label>
                        <input
                            type="date"
                            value={dateCreation}
                            onChange={(e) => setDateCreation(e.target.value)}
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px'
                            }}
                            required
                        />
                    </div>
                    <div style={{ display: 'flex', flexDirection: 'column' }}>
                        <label>Type de Compte :</label>
                        <select
                            value={typeCompte}
                            onChange={(e) => setTypeCompte(e.target.value)}
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                borderRadius: '4px',
                                marginBottom: '10px'
                            }}
                        >
                            <option value="COURANT">Courant</option>
                            <option value="EPARGNE">Épargne</option>
                        </select>
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        style={{
                            backgroundColor: '#4CAF50',
                            color: 'white',
                            padding: '12px 20px',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            border: 'none',
                            marginTop: '10px'
                        }}
                    >
                        {loading ? 'Ajout en cours...' : 'Ajouter'}
                    </button>
                    {error && <p style={{ color: 'red', marginTop: '10px' }}>Erreur : {error.message}</p>}
                </form>
                <button
                    onClick={() => window.location.reload()}
                    style={{
                        color: '#007bff',
                        marginTop: '20px',
                        textDecoration: 'underline',
                        cursor: 'pointer'
                    }}
                >
                    Voir tous les comptes
                </button>
            </div>
        </div>
    );
};

export default AddCompte;
